import React, { useState, useEffect } from 'react';
import './bootstrap/css/bootstrap.min.css';
import Navbar from './components/Navbar';
import FilterForm from './components/FilterForm';
import PersonCard from './components/PersonCard';
import axios from 'axios'; // Importe a biblioteca axios

const App = () => {
  const [nameFilter, setNameFilter] = useState('');
  const [persons, setPersons] = useState([]);

  // Função para buscar os dados da API
  const fetchFBIWantedList = async () => {
    try {
      const response = await axios.get(
        'https://vigilant-api-a2xyukeyka-uc.a.run.app/fbi/getallwanted?page=1'
      );
      if (response.status === 200) {
        // Mapeie os dados da resposta para o formato desejado
        const data = response.data.map((item) => ({
          id: item.id,
          title: item.title,
          subjects: item.subjects,
          images: item.images,
          url: item.url,
        }));
        setPersons(data);
      }
    } catch (error) {
      console.error('Erro ao buscar os dados da API:', error);
    }
  };

  // Use useEffect para buscar os dados da API quando o componente for montado
  useEffect(() => {
    fetchFBIWantedList();
  }, []);

  return (
    <>
      <Navbar />
      <div className="row">
        <FilterForm nameFilter={nameFilter} setNameFilter={setNameFilter} />
        <div className="col-md-10" id="wanted-persons">
          {persons.map((person) => (
            <PersonCard key={person.id} person={person} />
          ))}
        </div>
      </div>
    </>
  );
};

export default App;
