import React, { useState, useEffect } from 'react';
import FbiPersonCard from './FbiPersonCard';
import PaginationComponent from './PaginationComponent';

const FbiPage = () => {
  const [fbiPersons, setFbiPersons] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);  // Adicione esta linha
  const [currentPage, setCurrentPage] = useState(0);
  const itemsPerPage = 15;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://vigilant-api-a2xyukeyka-uc.a.run.app/fbi/getallwanted?page=${currentPage + 1}`
        );
        if (response.ok) {
          const data = await response.json();
          setFbiPersons(data.wantedList);  // Atualize esta linha
          setTotalRecords(data.totalRecords);  // Adicione esta linha
        }
      } catch (error) {
        console.error('Erro ao buscar dados do FBI:', error);
      }
    };

    fetchData();
  }, [currentPage]);

  const handlePageChange = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
  };

  return (
    <div className="container mt-3">
      <div className="row">
        {fbiPersons.map((person) => (
          <div key={person.id} className="col-md-4">
            <FbiPersonCard person={person} />
          </div>
        ))}
      </div>
      <PaginationComponent
        pageCount={Math.ceil(totalRecords / itemsPerPage)}
        onPageChange={handlePageChange}
      />
    </div>
  );
};

export default FbiPage;
