import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import InterpolPersonCard from './InterpolPersonCard';
import ReactPaginate from 'react-paginate';

const InterpolPage = () => {
  const [interpolPersons, setInterpolPersons] = useState([]);
  const [totalRecords, setTotalRecords] = useState(0);
  const itemsPerPage = 15;
  const navigate = useNavigate();
  const location = useLocation();

  // Pegue a página atual da URL.
  const currentPage = new URLSearchParams(location.search).get('page') || 1;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://vigilant-api-a2xyukeyka-uc.a.run.app/interpol/getallnotices?page=${currentPage}`
        );
        if (response.ok) {
          const data = await response.json();
          setInterpolPersons(data.notices);
          setTotalRecords(data.totalRecords);
        }
      } catch (error) {
        console.error('Erro ao buscar dados da Interpol:', error);
      }
    };

    fetchData();
  }, [currentPage]);

  const handlePageChange = (selectedPage) => {
    const nextPage = selectedPage.selected + 1;
    navigate(`?page=${nextPage}`);
  };

  return (
    <div className="container mt-3">
      <div className="row">
        {interpolPersons.map((person) => (
          <div key={person.id} className="col-md-4">
            <InterpolPersonCard person={person} />
          </div>
        ))}
      </div>
      <div className="pagination-container">
        <ReactPaginate
            previousLabel={<button className="btn btn-secondary">Anterior</button>}
            nextLabel={<button className="btn btn-secondary">Próximo</button>}
            breakLabel={<div className="btn btn-light disabled">...</div>}
            pageCount={Math.ceil(totalRecords / itemsPerPage)}
            marginPagesDisplayed={2}
            pageRangeDisplayed={5}
            onPageChange={handlePageChange}
            containerClassName={'pagination justify-content-center'}
            pageClassName={'page-item'}
            pageLinkClassName={'page-link'}
            previousClassName={'page-item'}
            nextClassName={'page-item'}
            previousLinkClassName={'page-link'}
            nextLinkClassName={'page-link'}
            breakClassName={'page-item'}
            activeClassName={'active'}
            forcePage={currentPage - 1}
        />
        </div>

    </div>
  );
};

export default InterpolPage;
