import React, { useState, useEffect } from 'react';

const FbiFilterForm = ({ filters, setFilters, applyFilters }) => {
  const [searchTimeout, setSearchTimeout] = useState(null);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFilters((prevFilters) => ({ ...prevFilters, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    applyFilters();
  };

  // Função de busca com debounce
  const searchWithDebounce = (value) => {
    if (searchTimeout) {
      clearTimeout(searchTimeout);
    }

    const timeout = setTimeout(() => {
      applyFilters();
    }, 2000);

    setSearchTimeout(timeout);
  };

  // Monitorar mudanças nos campos de entrada
  useEffect(() => {
    searchWithDebounce(filters); // Chame a função de busca com debounce
  }, [filters, searchWithDebounce]);

  return (
    <div className="col justify-content-center align-items-center" id="filtros">
      <form onSubmit={handleSubmit}>
        {['title', 'subject', 'nationality', 'sex', 'race'].map((field, index) => (
          <div key={index} className="col mb-3">
            <label className="form-label" htmlFor={`${field}-filter`}>
              {field.charAt(0).toUpperCase() + field.slice(1)}
            </label>
            <input
              type="text"
              className="form-control"
              id={`${field}-filter`}
              name={field}
              value={filters[field]}
              onChange={handleInputChange}
            />
          </div>
        ))}
        <div className="col mb-3">
          <button type="submit" className="btn btn-primary">
            Submit
          </button>
        </div>
      </form>
    </div>
  );
};

export default FbiFilterForm;
