import React from 'react';
import Navbar from './components/Navbar';
import InterpolPage from './components/InterpolPage'; 
import FbiPage from './components/FbiPage';
import LoginComponent from './components/LoginComponent';
import RegisterComponent from './components/RegisterComponent';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import HomePage from './components/HomePage';
import { AuthenticationProvider, useAuth } from './contexts/AuthenticationContext.js';

const ProtectedRoute = ({ children }) => {
  const { isAuthenticated } = useAuth();

  if (!isAuthenticated) {
    return <Navigate to="/login" />;
  }

  return children;
};

const App = () => {
  return (
    <AuthenticationProvider>
      <Router>
        <Navbar />
        <div className="row mt-3">
          <div className="col" id="wanted-persons">
            <Routes>
              <Route path="/login" element={<LoginComponent />} />
              <Route path="/register" element={<RegisterComponent />} />
              <Route path="/fbi" element={
                <ProtectedRoute>
                  <FbiPage />
                </ProtectedRoute>
              } />
              <Route path="/interpol" element={
                <ProtectedRoute>
                  <InterpolPage />
                </ProtectedRoute>
              } />
              <Route path="/" element={<HomePage />} />
            </Routes>
          </div>
        </div>
      </Router>
    </AuthenticationProvider>
  );
};

export default App;
