import React, { Fragment } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import { useSelector } from 'react-redux'; // Importe o useSelector se você estiver usando o Redux
import Navbar from './components/Navbar';
import Home from './components/HomePage';
import Register from './components/RegisterComponent';
import Login from './components/LoginComponent';
import { AuthenticationProvider, useAuth } from './contexts/AuthenticationContext';

const Protected = ({ isAdmin, children }) => {
  const { currentUser } = useAuth(); // Ajuste conforme sua lógica de autenticação

  if (!currentUser) {
    return <Navigate to="/login" />;
  }

  if (isAdmin && currentUser.role !== 'admin') {
    return <Navigate to="/login" />;
  }

  return <Fragment>{children}</Fragment>;
};

const App = () => {
  return (
    <AuthenticationProvider>
      <Router>
        <Fragment>
          <Navbar />
          <Routes>
            <Route path="/" element={
              <Protected>
                <Home />
              </Protected>
            } />
            <Route path="/register" element={<Register />} />
            <Route path="/login" element={<Login />} />
          </Routes>
        </Fragment>
      </Router>
    </AuthenticationProvider>
  );
}

export default App;
