import React, { createContext, useContext, useState, useEffect } from 'react';

const AuthenticationContext = createContext();

export const useAuth = () => {
    return useContext(AuthenticationContext);
};

export const AuthenticationProvider = ({ children }) => {
    const [currentUser, setCurrentUser] = useState(null);

    // UseEffect para verificar se há um token válido no localStorage durante o carregamento inicial.
    useEffect(() => {
        const token = localStorage.getItem('token');
        if (token) {
            // Realize uma verificação adicional do token no servidor, se necessário.
            // Em caso afirmativo, atualize o estado do currentUser aqui.
        }
    }, []);

    const login = async (username, password) => {
        try {
            const response = await fetch('https://vigilant-api-a2xyukeyka-uc.a.run.app/Account/Login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ username, password }),
            });

            if (response.ok) {
                const data = await response.json();
                console.log(data.Message);
                localStorage.setItem('token', data.token);
                // Atualize o estado do currentUser após um login bem-sucedido.
                setCurrentUser({ fullName: data.fullName, token: data.token });
                console.log(currentUser); // Isso pode ser null devido ao encerramento do useEffect.
            } else {
                const data = await response.json();
                console.error(data.message);
                throw new Error(data.message);
            }
        } catch (error) {
            console.error("Erro ao fazer login", error);
            throw error;
        }
    };

    // Restante do código mantido sem alterações.
    
