import React, { useState, useEffect } from 'react';
import FbiPersonCard from './FbiPersonCard';
import ReactPaginate from 'react-paginate';

const FbiPage = () => {
  const [fbiPersons, setFbiPersons] = useState([]);
  const [currentPage, setCurrentPage] = useState(0); // Estado para controlar a página atual
  const itemsPerPage = 12; // Quantidade de itens por página

  useEffect(() => {
    // Função para buscar dados do FBI com base na página atual
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://vigilant-api-a2xyukeyka-uc.a.run.app/fbi/getallwanted?page=${currentPage + 1}`
        );
        if (response.ok) {
          const data = await response.json();
          setFbiPersons(data);
        }
      } catch (error) {
        console.error('Erro ao buscar dados do FBI:', error);
      }
    };

    fetchData();
  }, [currentPage]);

  // Função para lidar com a mudança de página
  const handlePageChange = (selectedPage) => {
    setCurrentPage(selectedPage.selected);
  };

  return (
    <div className="container mt-3">
      <div className="row">
        {fbiPersons.map((person) => (
          <div key={person.id} className="col-md-4">
            <FbiPersonCard person={person} />
          </div>
        ))}
      </div>
      <div className="pagination-container">
        <ReactPaginate
          previousLabel={'Anterior'}
          nextLabel={'Próximo'}
          breakLabel={'...'}
          pageCount={Math.ceil(fbiPersons.length / itemsPerPage)}
          marginPagesDisplayed={2}
          pageRangeDisplayed={5}
          onPageChange={handlePageChange}
          containerClassName={'pagination'}
          activeClassName={'active'}
        />
      </div>
    </div>
  );
};

export default FbiPage;
