import React from 'react';
import { Navigate, Outlet } from 'react-router-dom';
import { useAuth } from '../contexts/AuthenticationContext'; // Ajuste o caminho conforme necessário

const PrivateRoute = () => {
    const { currentUser } = useAuth(); // Buscar o usuário atual do contexto de autenticação

    // Se autorizado, retorna um outlet que renderizará elementos filhos
    // Se não, retorna um elemento que navegará para a página de login
    return currentUser ? <Outlet /> : <Navigate to="/login" />;
}

export default PrivateRoute;
